
public class 반복문_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 10; i>=0; i--) {
			System.out.print(i + " ");
		}
		for(int i= 'A'; i<='Z'; i++) {
			System.out.print((char) i);
		}
		//A~Z
		
		for(int i= 'Z'; i>='A'; i--) {
			System.out.print((char) i);
		}
		//Z~A
	}

}
